const randomInt = function(min, max) {
    return Math.floor(min + Math.random() * (max+1 - min));
}
const imgArray = [
    "https://source.unsplash.com/collection/365/1920x1080",
    "https://source.unsplash.com/collection/1244/1920x1080",
    "https://source.unsplash.com/collection/256443/1920x1080",
    "https://source.unsplash.com/collection/296884/1920x1080",
    "https://source.unsplash.com/collection/335772/1920x1080",
    "https://source.unsplash.com/collection/351720/1920x1080",
    "https://source.unsplash.com/collection/513691/1920x1080",
    "https://source.unsplash.com/collection/991244/1920x1080",
    "https://source.unsplash.com/collection/1125088/1920x1080",
    "https://source.unsplash.com/collection/1131027/1920x1080",
    "https://source.unsplash.com/collection/1127805/1920x1080",
    "https://source.unsplash.com/collection/1254642/1920x1080",
    "https://source.unsplash.com/collection/197390/1920x1080"
];

const getRandomCssString = function() {
    let len = imgArray.length;
    return 'url("' + imgArray[randomInt(0, len-1)] + '")';
};
document.body.style.backgroundImage = getRandomCssString();
